if (!defined('_PS_VERSION_'))
	exit;

class Stickngo extends Module
{
	protected $config_form = false;

	public function __construct()
	{
		$this->name = 'stickngo';
		$this->tab = 'shipping_logistics';
		$this->version = '1.0.1';
		$this->author = 'Avery';
		$this->need_instance = 0;

		/**
		 * Set $this->bootstrap to true if your module is compliant with bootstrap (PrestaShop 1.6)
		 */
		$this->bootstrap = true;

		parent::__construct();

		$this->displayName = $this->l('stickngo');
		$this->description = $this->l('The Stick&Go sheets allow you to have in a unique document your order form and your delivery label!');
		$this->confirmUninstall = $this->l(' Module(s) uninstalled successfully');
	}

	/**
	 * Load the configuration form
	 */
	public function getContent()
	{
		$this->context->smarty->assign('module_dir', $this->_path);

		$output = $this->context->smarty->fetch($this->local_path.'views/templates/admin/configure.tpl');

		return $output;
	}
}
